var MD5 = require('./lib/md5.js');
var SHA1 = require('./lib/sha1.js');

exports.md5 = function(openPassword){
   return MD5.MD5(openPassword);
};
exports.sha1 = function(openPassword){
   return SHA1.SHA1(openPassword);
};

exports.compare = function(enterPassword, closedPassword) {

    if(enterPassword == closedPassword) {
        return true;
    } else {
        return false;
    }
}